import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:4000/api';

const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('accessToken');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor
api.interceptors.response.use(
  (response) => response,
  async (error) => {
    const originalRequest = error.config;

    if (error.response?.status === 401 && !originalRequest._retry) {
      originalRequest._retry = true;

      try {
        const refreshToken = localStorage.getItem('refreshToken');
        const response = await axios.post(
          `${API_URL}/auth/refresh-token`,
          { refreshToken }
        );

        const { accessToken, refreshToken: newRefreshToken } = response.data;
        localStorage.setItem('accessToken', accessToken);
        localStorage.setItem('refreshToken', newRefreshToken);

        originalRequest.headers.Authorization = `Bearer ${accessToken}`;
        return api(originalRequest);
      } catch (refreshError) {
        localStorage.removeItem('accessToken');
        localStorage.removeItem('refreshToken');
        window.location.href = '/';
        return Promise.reject(refreshError);
      }
    }

    return Promise.reject(error);
  }
);

export const authAPI = {
  register: (data) => api.post('/auth/register', data),
  requestOTP: (data) => api.post('/auth/request-otp', data),
  verifyOTP: (data) => api.post('/auth/verify-otp', data),
  getMe: () => api.get('/auth/me'),
};

export const friendsAPI = {
  search: (query) => api.get(`/friends/search?query=${query}`),
  sendRequest: (receiverId) => api.post('/friends/request', { receiverId }),
  getPending: () => api.get('/friends/requests/pending'),
  getSent: () => api.get('/friends/requests/sent'),
  acceptRequest: (requestId) => api.post(`/friends/request/accept/${requestId}`),
  rejectRequest: (requestId) => api.post(`/friends/request/reject/${requestId}`),
  cancelRequest: (requestId) => api.delete(`/friends/request/cancel/${requestId}`),
  getList: () => api.get('/friends/list'),
  remove: (friendId) => api.delete(`/friends/${friendId}`),
};

export const chatAPI = {
  sendMessage: (data) => api.post('/chat/send', data),
  getMessages: (friendId) => api.get(`/chat/messages/${friendId}`),
  deleteMessage: (messageId) => api.delete(`/chat/messages/${messageId}`),
};

export default api;
